namespace AdventOfCode.Puzzles.Day15;

using BenchmarkDotNet.Attributes;
using BenchmarkDotNet.Jobs;
using NUnit.Framework;

[MemoryDiagnoser(false)]
[SimpleJob(RuntimeMoniker.Net90, baseline: true, iterationCount: 20)]
public class Puzzle1
{
    [Test]
    [Benchmark]
    public void Solve()
    {
        var lines = PuzzleInput.Split('\n', StringSplitOptions.RemoveEmptyEntries);
        var grid = lines.Where(x => x.StartsWith('#')).Select(x => x.ToCharArray()).ToArray();
        var moves = lines.Where(x => !x.StartsWith('#')).SelectMany(x => x.ToCharArray()).ToArray();

        bool ValidPosition(int x, int y) =>
            x >= 0 && x < grid.Length && y >= 0 && y < grid[0].Length;

        var directions = new Dictionary<char, (int dx, int dy)>
        {
            ['^'] = (-1, 0),
            ['>'] = (0, 1),
            ['v'] = (1, 0),
            ['<'] = (0, -1)
        };

        var (x, y) = grid.Select((row, x) => (x, y: Array.IndexOf(row, '@'))).First(x => x.y >= 0);

        foreach (var move in moves)
        {
            var (dx, dy) = directions[move];

            var nx = x + dx;
            var ny = y + dy;

            if (ValidPosition(nx, ny))
            {
                var neighbour = grid[nx][ny];
                if (neighbour is '.')
                {
                    grid[nx][ny] = '@';
                    grid[x][y] = '.';

                    x = nx;
                    y = ny;
                }
                else if (neighbour is 'O')
                {
                    var nnx = nx + dx;
                    var nny = ny + dy;

                    while (ValidPosition(nnx, nny) && grid[nnx][nny] is 'O')
                    {
                        nnx += dx;
                        nny += dy;
                    }

                    if (grid[nnx][nny] is '#')
                    {
                        continue;
                    }

                    grid[nnx][nny] = 'O';
                    grid[nx][ny] = '@';
                    grid[x][y] = '.';

                    x = nx;
                    y = ny;
                }
            }

            // Console.WriteLine("After move " + moveCount++ + ": " + move);
            // Console.WriteLine(string.Join('\n', grid.Select(g => new string(g))));
            // Console.WriteLine();
        }

        var gps = 0L;

        for (var i = 0; i < grid.Length; i++)
        {
            for (var j = 0; j < grid[0].Length; j++)
            {
                if (grid[i][j] is 'O')
                {
                    gps += i * 100 + j;
                }
            }
        }

        Console.WriteLine(gps);
    }

